import React, { useState, useEffect } from "react";
import "./EditProfile.css";
import { doc, getDoc, query, where, collection, getDocs } from "firebase/firestore";
import { db } from "../../config/firestore";
import { getAuth, sendSignInLinkToEmail } from "firebase/auth";

function EditProfile() {
  const auth = getAuth();
  const currentUser = auth.currentUser;
  console.log("Current user:", currentUser);

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [cardNumber, setCardNumber] = useState("");
  const [cvv, setCVV] = useState("");
  const [expirationDate, setExpirationDate] = useState("");
  const [billingAddress, setBillingAddress] = useState("");


  useEffect(() => {
    const fetchUserData = async () => {
      if (currentUser) {
        const userQuery = query(
          collection(db, "user"),
          where("uid", "==", currentUser.uid)
        );
        const querySnapshot = await getDocs(userQuery);
        if (!querySnapshot.empty) {
          const userDoc = querySnapshot.docs[0]; // Assuming there's only one document with this UID
          setFirstName(userDoc.data().fname);
          setLastName(userDoc.data().lname);
          setEmail(userDoc.data().email);
          setPassword(userDoc.data().passwd);
          setCardNumber(userDoc.data().cardNumber);
          setCVV(userDoc.data().cvv);
          setExpirationDate(userDoc.data().setExpirationDate);
          setBillingAddress(userDoc.data().billingAddress);
          console.log("User data: ", userDoc.data()); // to see if it actully fetched 
        } else {
          console.log("No such user!");
        }
      }
    };
    fetchUserData();
  }, [currentUser]);

  const handleSave = async () => {
    if (currentUser) {
      const userRef = doc(db, "user", currentUser.uid);
      await updateDoc(userRef, {
        fname: firstName,
        lname: lastName,
        passwd: password,
        cardNumber: cardNumber,
        cvv: cvv,
        expirationDate: expirationDate,
        billingAddress: billingAddress,
      });
      console.log("Profile updated successfully");
      setEditMode(false); // Exit edit mode after saving
    }
  };

  return (
    <div className="editProfile">
      <h1>Edit Profile</h1>
      <h2>Account Details</h2>
      <div>
        <p>First Name: {firstName}</p>
        <p>Last Name: {lastName}</p>
        <p>Email: {email}</p>
        <p>Password: {password}</p>
        </div>
        <h2>Financial Details</h2>
        <div>
        <p>Card Number: {cardNumber}</p>
        <p>CVV: {cvv}</p>
        <p>Expiration Date: {expirationDate}</p>
        <p>Billing Address: {billingAddress}</p>
        </div>
    </div>
  );
}

export default EditProfile;
