//import React from 'react';
import React, { useContext, useState } from "react";
import "./Login.css";
import "./Button.css";
import ForgotPassword from "./ForgotPassword";
import Button from "./Button";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import UserContext from "../context/UserContext";
import Signup from "./Signup";
import Header from "./Header";
//import { loginUser } from "./User";

function Login({ toggle, updateToken }) {
  // used https://dev.to/afromatt6288/create-a-popup-form-for-login-and-then-style-it-37jl
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [usernameadmin, setUsernameAdmin] = useState("");
  const [passwordadmin, setPasswordAdmin] = useState("");

  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  //const navigate = useNavigate();
  const { setUserData } = useContext(UserContext);
  const [forgotPasswordSeen, setForgotPasswordSeen] = useState(false);

  const toggleForgotPassword = () => {
    setForgotPasswordSeen(!forgotPasswordSeen);
  };

  function httpGetSync(theUrl, callback) {
    var xmlHttp = new XMLHttpRequest();
    xmlHttp.onreadystatechange = function () {
      if (xmlHttp.readyState == 4 && xmlHttp.status == 200)
        callback(xmlHttp.responseText);
    };
    xmlHttp.open("GET", theUrl, false); // true for asynchronous
    xmlHttp.send(null);
  } // httpGetAsync

  async function handleLogin(e) {
    e.preventDefault();
    setLoading(true);

    try {
      // keeps user logged in

      const loginRes = await axios.get(
        "http://csci4050.heliumyang.com:2999/verifylogin",
        {
          email,
          passwd,
        }
      );
      var userData;

      const oldUser = {
        email: email,
        passwd: password,
        phone: number,
      };

      const queryString = Object.keys(oldUser)
        .map((key) => key + "=" + oldUser[key])
        .join("&");
      httpGetSync(
        `http://csci4050.heliumyang.com:2999/register/?${queryString}`,
        function (data) {
          userData = data;
        }
      );

      setUserData({
        token: userData.token,
        user: userData.user,
      });

      localStorage.setItem("auth-token", userData.token);
      updateToken();
      setLoading(false);

      toggle();
    } catch (err) {
      setLoading(false);

      console.error("Login error:", err.response); // error message in console

      // for 404 error: usually wrong username or password
      if (err.response && err.response.status === 400) {
        setError(
          "Invalid credentials. Please check your username and password."
        );
      }
    }
  }

  console.log("Error state:", error);

  return (
    <div className="popup">
      <div className="popup-inner">
        <h2>Login</h2>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleLogin}>
          <label className="field">
            Email:
            <input
              required
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </label>

          <label className="field">
            Password:
            <input
              required
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <Button className="btn" type="submit">
            Login
          </Button>
        </form>
        <Button className="btn" onClick={toggle}>
          Close
        </Button>
        <br />
        <Button onClick={toggleForgotPassword}>Forgot your password?</Button>
        {forgotPasswordSeen ? (
          <ForgotPassword
            updateToken={updateToken}
            toggle={toggleForgotPassword}
          />
        ) : null}
      </div>
    </div>
  );
}

export default Login;
